import React, { FC, useState } from 'react'; //FC is functional component
import './App.css';

//this code gets inserted in the index.tsx file
const App: FC = () => {
  return (
    <div>
      <div className="box" id="heading">
        <h1> To Do List </h1> 
      </div> {/* end heading div */}

      <div className="box">
        <div id="myTasks">
          {/* where items will be added when they are inputted */}
        </div> {/* end added Items div */}

        <div className="item">
          <input type="text" id="input" placeholder="Add Task..."/>
          <button type="submit" className="addBtn addBtn-1"> + </button> 
        </div> {/* end input and button div */}
      </div>

    </div> //end BIG Div
  );
}

export default App;
